import React, { useState, useEffect } from "react";
import "./styles.css";

/**
 * Home screen illustration with welcome text
 */
const defaultScreen = (
  <div className="home-screen">
    <img src="/illustration.svg" alt="Illustration" className="home-illustration" />
    <h1>Pocket Notes</h1>
    <p className="subtitle">
      Send and receive messages without keeping your phone online.
    </p>
    <p className="description">
      Use Pocket Notes on up to 4 linked devices and 1 mobile phone
    </p>
  </div>
);

function App() {
  // State for storing groups (e.g., [{ name: "Work", color: "#F00" }])
  const [groups, setGroups] = useState(() => JSON.parse(localStorage.getItem("groups")) || []);

  // State for the currently active group
  const [activeGroup, setActiveGroup] = useState(null);

  // Notes are stored per group as an object: { [groupName]: [noteObjects] }
  const [notes, setNotes] = useState(() => JSON.parse(localStorage.getItem("notes")) || {});

  // New note input field
  const [newNote, setNewNote] = useState("");

  // Modal visibility for creating a new group
  const [showModal, setShowModal] = useState(false);

  // Input fields in modal
  const [groupName, setGroupName] = useState("");
  const [groupColor, setGroupColor] = useState("#FFD700");

  // Save groups and notes to localStorage on every change
  useEffect(() => {
    localStorage.setItem("groups", JSON.stringify(groups));
    localStorage.setItem("notes", JSON.stringify(notes));
  }, [groups, notes]);

  // Create a new group with the specified name and color
  const handleCreateGroup = () => {
    if (!groupName.trim()) return;
    const newGroup = { name: groupName.trim(), color: groupColor };
    setGroups([...groups, newGroup]);
    setShowModal(false);
    setGroupName("");
    setGroupColor("#FFD700");
  };

  // Send a new note to the current group
  const handleSendNote = () => {
    if (!newNote.trim()) return;
    const groupNotes = notes[activeGroup.name] || [];
    const updatedNotes = {
      ...notes,
      [activeGroup.name]: [
        ...groupNotes,
        {
          text: newNote.trim(),
          timestamp: new Date().toLocaleString(),
        },
      ],
    };
    setNotes(updatedNotes);
    setNewNote("");
  };

  return (
    <div className="app">
      {/* Sidebar: List of groups */}
      <aside className="sidebar">
        <h2 className="sidebar-title">Pocket Notes</h2>
        <div className="group-list">
          <div>Hello World</div>
          {groups.map((group, index) => (
            <div
              key={index}
              className={`group-item ${activeGroup?.name === group.name ? "active" : ""}`}
              onClick={() => setActiveGroup(group)}
            >
              <div
                className="group-avatar"
                style={{ backgroundColor: group.color }}
              >
                {group.name.charAt(0).toUpperCase()}
              </div>
              <span className="group-name">{group.name}</span>
            </div>
          ))}
        </div>
        <button className="create-btn" onClick={() => setShowModal(true)}>
          + Create Notes Group
        </button>
      </aside>

      {/* Main Note Area */}
      <main className="note-area">
        {activeGroup ? (
          <div className="note-panel">
            <header className="note-header">
              <div
                className="group-avatar"
                style={{ backgroundColor: activeGroup.color }}
              >
                {activeGroup.name.charAt(0).toUpperCase()}
              </div>
              <h3 className="note-title">{activeGroup.name}</h3>
            </header>

            <div className="note-list">
              {(notes[activeGroup.name] || []).map((note, index) => (
                <div key={index} className="note-card">
                  <p className="note-text">{note.text}</p>
                  <span className="note-timestamp">{note.timestamp}</span>
                </div>
              ))}
            </div>

            <div className="note-input">
              <textarea
                className="note-textarea"
                placeholder="Enter your text here..."
                value={newNote}
                onChange={(e) => setNewNote(e.target.value)}
              ></textarea>
              <button className="send-btn" onClick={handleSendNote}>
                Send
              </button>
            </div>
          </div>
        ) : (
          defaultScreen
        )}
      </main>

      {/* Modal for creating new group */}
      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h3 className="modal-title">Create New Notes Group</h3>
            <input
              type="text"
              placeholder="Group Name"
              className="modal-input"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
            <input
              type="color"
              className="modal-color"
              value={groupColor}
              onChange={(e) => setGroupColor(e.target.value)}
            />
            <button className="modal-create-btn" onClick={handleCreateGroup}>
              Create
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
